import React, { useState } from 'react';
import { ArrowLeft, Camera, Save } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import './personalinfo.css'

const defaultProfileImage = 'data:image/png;base64,';

const PersonalInfo = ({ onBack }) => {
  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);
  const [imagePreview, setImagePreview] = useState(null);
  const [formData, setFormData] = useState({
    name: 'Hadi Nhayli',
    email: 'hadi.nhayli@example.com',
    phone: '+1 234 567 8900',
    dateOfBirth: '1990-01-01',
    gender: 'Male',
    bloodType: 'O+'
  });

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e?.preventDefault();
    setIsEditing(false);
  };

  return (
    <div className="personal-info-page">
      <div className="header-section">
        <div className="header-top">
          <button className="back-button" onClick={() => navigate(-1)}>
            <ArrowLeft className="icon" />
          </button>
          <h1>Personal Information</h1>
          <button
            className="edit-button"
            onClick={() => isEditing ? handleSubmit() : setIsEditing(true)}
          >
            {isEditing ? (
              <>
                <Save className="icon" />
                <span>Save</span>
              </>
            ) : (
              <span>Edit</span>
            )}
          </button>
        </div>
        
        <div className="profile-image-container">
          <div className="profile-imagee">
            <img 
              src={defaultProfileImage || "/api/placeholder/150/150"} 
              alt="Profile" 
            />
            {isEditing && (
              <label className="camera-icon">
                <Camera />
                <input 
                  type="file" 
                  accept="image/*" 
                  onChange={handleImageChange} 
                  style={{ display: 'none' }} 
                />
              </label>
            )}
          </div>
        </div>
      </div>

      <div className="content-section">
        <form className="info-form" onSubmit={handleSubmit}>
          <div className="personalform-groupp">
            <label>Full Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              readOnly={!isEditing}
              className={!isEditing ? 'readonly' : ''}
            />
          </div>

          <div className="personalform-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              readOnly={!isEditing}
              className={!isEditing ? 'readonly' : ''}
            />
          </div>

          <div className="personalform-group">
            <label>Phone Number</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              readOnly={!isEditing}
              className={!isEditing ? 'readonly' : ''}
            />
          </div>

          <div className="personalform-group">
            <label>Date of Birth</label>
            <input
              type="date"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleInputChange}
              readOnly={!isEditing}
              className={!isEditing ? 'readonly' : ''}
            />
          </div>

          <div className="personalform-group">
            <label>Gender</label>
            {isEditing ? (
              <select 
                name="gender" 
                value={formData.gender}
                onChange={handleInputChange}
              >
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            ) : (
              <input
                type="text"
                value={formData.gender}
                readOnly
                className="readonly"
              />
            )}
          </div>

          <div className="personalform-group">
            <label>Blood Type</label>
            {isEditing ? (
              <select 
                name="bloodType" 
                value={formData.bloodType}
                onChange={handleInputChange}
              >
                {['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'].map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>
            ) : (
              <input
                type="text"
                value={formData.bloodType}
                readOnly
                className="readonly"
              />
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default PersonalInfo;